import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css'],
})
export class ProductDetailsComponent implements OnInit {
  productData: any = null;

  constructor(private route: ActivatedRoute) {}

  ngOnInit(): void {
    // Retrieve the parameter from the route
    this.productData = this.route.snapshot.paramMap.get('productData');
  }
}
<div>
<div class="toolbar">
  <p class="products-text">Product Details</p>
 </div>
  <div class="card-container">
    <div class="card">
    <mat-card>
      <mat-card-header>
        <mat-card-title>{{ item.title }}</mat-card-title>
      </mat-card-header>
      <mat-card-content>
        {{ item.description }}
      </mat-card-content>
      <mat-card-content class="products-title">
        Rs. {{ item.price }}
      </mat-card-content>
       <mat-card-actions>
    <button mat-button (click)="updateProduct()">Update</button>
    <div class="margin-left10"></div>
    <button mat-button class="delete-text" (click)="deleteProduct()">Delete</button>
  </mat-card-actions>
    </mat-card>
  </div>
</div>
