import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';

@Component({
  selector: 'app-update-product',
  templateUrl: './update-product.component.html',
  styleUrls: ['./update-product.component.css'],
})
export class UpdateProductComponent {
  data: any;
  deleteProductResponse: any;
  updateProductResponse: any;
  constructor(
    private apiService: ApiService,
    private _snackBar: MatSnackBar,
    private router: Router
  ) {}
  updateProduct(payload: any, id: number): void {
    this.apiService.updateProductData(id, payload).subscribe(
      (response) => {
        this.updateProductResponse = response;
        console.log('updated data:', this.updateProductResponse);
        this.showAlert('Product updated successfully');
      },
      (error) => {
        console.error('Error while updating data:', error);
        this.showAlert('Error while updating data, Please try again');
      }
    );
  }
  showAlert(message: string): void {
    this._snackBar.open(message, 'Close', {
      duration: 3000,
      verticalPosition: 'top',
      horizontalPosition: 'center',
    });
  }
}
<div>
  <div class="toolbar">
    <p class="products-text">Update Product</p>
  </div>
  <div class="card-container">
    <form [formGroup]="myForm" (ngSubmit)="updateProductDetails()" class="card">
        <div class="rows"> 
        <label for="name">Product Name:</label>
        <input type="text" id="name" formControlName="name" required>
        </div>
        <div class="rows">
        <label for="name">Product Description:</label>
        <input type="text" id="description" formControlName="description" required>
        </div>
        <div class="rows">
        <label for="name">Price:</label>
        <input type="text" id="price" formControlName="price" required>
        </div>
        <div class="rows">
        <label for="name">Category:</label>
        <input type="text" id="category" formControlName="category" required>
        </div>
        <div class="rows">
        <label for="name">Stock: </label>
        <input type="text" id="stock" formControlName="stock" required>
        </div>
        <div class="rows">
        <label for="name">Brand:</label>
        <input type="text" id="brand" formControlName="brand" required>
        </div>
        <button mat-button>Update Product</button>
    </form>

  </div>
</div>
