export class ProductsComponent {
  cards = [
    {
      title: 'Handcraft Chinese style',
      description:
        'Handcraft Chinese style art luxury palace hotel villa mansion home decor ceramic vase with brass fruit plate',
      price: '60',
      category: 'home-decoration',
