import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css'],
})
export class AddProductComponent {
  myForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.myForm = this.fb.group({
      name: ['', Validators.required],
      description: ['', Validators.required],
      price: ['', Validators.required],
      stock: ['', Validators.required],
      brand: ['', Validators.required],
      category: ['', Validators.required],
    });
  }

  addProductDetails() {
    if (this.myForm.valid) {
      // Process the form data
      const formData = this.myForm.value;
      console.log('formData is >>', formData);
      // Add logic to send data to the server or perform other actions
    }
  }
}
<div>
  <div class="toolbar">
    <p class="products-text">Add Product</p>
  </div>
  <div class="card-container">
    <form [formGroup]="myForm" (ngSubmit)="addProductDetails()" class="card">
        <div> 
        <label for="name">Product Name:</label>
        <input type="text" id="name" formControlName="name" required>
        </div>
        <div>
        <label for="name">Product Description:</label>
        <input type="text" id="description" formControlName="description" required>
        </div>
        <div>
        <label for="name">Price:</label>
        <input type="text" id="price" formControlName="price" required>
        </div>
        <div>
        <label for="name">Category:</label>
        <input type="text" id="category" formControlName="category" required>
        </div>
        <div>
        <label for="name">Stock: </label>
        <input type="text" id="stock" formControlName="stock" required>
        </div>
        <div>
        <label for="name">Brand:</label>
        <input type="text" id="brand" formControlName="brand" required>
        </div>
        <button mat-button>Add Product</button>
    </form>

  </div>
</div>
