import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css'],
})
export class ProductDetailsComponent implements OnInit {

  constructor(private route: ActivatedRoute, private router: Router) {}

  ngOnInit(): void {
    // Retrieve the parameter from the route
    const navigation = this.router.getCurrentNavigation();
    const productData = navigation?.extras?.productData;

    if (productData) {
      const productData = productData;
      console.log('yyyy', this.productData);
      // Now, 'product' contains the object passed from the previous component.
    }
  }
}
<div>
  <div class="toolbar">
    <p class="products-text">Product Details</p>
  </div>
  <div class="card-container">
    <div class="card">
      <mat-card>
        <mat-card-header>
          <mat-card-title>{{ productData.title }}</mat-card-title>
        </mat-card-header>
        <mat-card-content>
          {{ productData.description }}
        </mat-card-content>
        <mat-card-content class="products-title">
          Rs. {{ productData.price }}
        </mat-card-content>
        <mat-card-actions> </mat-card-actions>
      </mat-card>
    </div>
  </div>
</div>
