import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css'],
})
export class ProductsComponent implements OnInit {
  data: any;
  constructor(private apiService: ApiService) {}
  ngOnInit(): void {
    this.apiService.getProductData().subscribe(
      (response) => {
        this.data = response?.products;
        console.log('Data from API:', this.data);
      },
      (error) => {
        console.error('Error fetching data:', error);
      }
    );
  }
  groupCardsIntoRows(data: any[], cardsPerRow: number): any[][] {
    const rows = [];
    for (let i = 0; i < data?.length; i += cardsPerRow) {
      rows.push(data.slice(i, i + cardsPerRow));
    }
    return rows;
  }
  updateProduct(): void {
    // Your action on button click goes here
    console.log('Update Button clicked!');
  }
  deleteProduct(): void {
    // Your action on button click goes here
    console.log('Delete Button clicked!');
  }
}
